FlowPanel.css:
.&form;-table {border-collapse:collapse; width:100%;}
.&form;-table td {border:1px solid black; width:calc(100%/2);padding-top: 0 !important;}
.&form;-forwarder .bee-co-companyInfo-name .bee-co-companyInfo-item:before {content: "Vežėjas: "; font-weight:normal;}
.&form;-customer .bee-co-companyInfo-name .bee-co-companyInfo-item:before {content: "Užsakovas: "; font-weight:normal;}
.&form;-customer .bee-co-companyInfo-mobile, .&form;-customer .bee-co-companyInfo-bankaccount {display:none;}
.&form;-LoadingDate, .&form;-UnloadingDate {display:inline-block; margin-right:10px;}

render.ui:function:
var descr = '';

if (!row.CargoDescription) {
  if (row.Description) {
    descr = descr + row.Description + '\n';
  }
  if (row.Quantity) {
    descr = descr + 'Kiekis: ' + row.Quantity + (row.QuantityUnit ? ' ' + row.QuantityUnit : '') + '\n';
  }
  if (row.Weight) {
    descr = descr + 'Svoris: ' + row.Weight + (row.WeightUnit ? ' ' + row.WeightUnit : '') + '\n';
  }
  if (row.Volume) {
    descr = descr + 'Tūris: ' + row.Volume + (row.VolumeUnit ? ' ' + row.VolumeUnit : '') + '\n';
  }
} else {
  descr = row.CargoDescription;
}
return descr;
